import React, {useState} from 'react'
import {View, Text, StyleSheet, Button} from 'react-native'

export default function Home(props) { //Props ne mozemo menjati, sta prosledimo u zagradama to uvek stoji, zbog toga mozemo koristiti state
  const [name, setName] = useState('BoxBox') //Varijabla i funkcija koje koristimo da pristupamo/menjamo ime. Ovaj metod se zove hooks
  //Ovde je onPress realizovano funkcionalno
  return (
    <View>
        <Text style= {{color:'red', backgroundColor:'yellow'}}>Welcome to {props.name} Python flask and React native</Text>
        <Text style = {styles.textStyle}>{name}</Text>
        <Button title = "Click" onPress={()=>setName("This is changed")}/>
    </View>
  )
}

const styles = StyleSheet.create({
    textStyle:{
        color:'blue',
        padding:10,
        margin:20,
        fontSize:25
    }
})
