import React, {useState} from 'react'
import {View, Text, StyleSheet, Button, FlatList} from 'react-native'
import {Card} from 'react-native-paper'
export default function Home(props) { //Props ne mozemo menjati, sta prosledimo u zagradama to uvek stoji, zbog toga mozemo koristiti state
  
  const data = [
    {id:1, title:'First Title', body: 'First Body'},
    {id:2, title:'Second Title', body: 'Second Body'},
    {id:3, title:'Third Title', body: 'Third Body'}
    
  ]

  const renderData = (item) => {
      return(
        <Card>
          <Text>{item.title}</Text>
          <Text>{item.body}</Text>
        </Card>
      )
  }
  //Ovde je onPress realizovano funkcionalno
  //Za flat list uvek prvo moramo odrediti koja mu je data
  //Render nam suzi da bi flat lista mogla da zna kad se nesto promenilo da se ponovo renderuje
  return (
    <View>
        <FlatList
          data = {data} 
          renderItem = {(item)=>{return renderData(item)}}
          keyExtracotr = {item => '${item.id}'}
        />
    
    
    </View>
  )
}

const styles = StyleSheet.create({
    textStyle:{
        color:'blue',
        padding:10,
        margin:20,
        fontSize:25
    }
})
